public class Practice17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 1; i < 11; i++)
		{
			for(int j = 1; j < 11; j++)
			{
				if(j < 3)
					System.out.print(i * j + "\t");
				else
					System.out.print(i * j + "\t");
			}
			System.out.println();
		}
	}

}