public class Practice18 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 1; i < 10; i++)
		{
			for(int j = 2; j < 6; j++)
			{
				System.out.print(j + " * " + i + " = " + i*j + "\t");
			}
			System.out.print("\n");
		}
		System.out.print("\n");
		for(int i = 1; i < 10; i++)
		{
			for(int j = 6; j < 10; j++)
			{
				System.out.print(j + " * " + i + " = " + i*j + "\t");
			}
			System.out.print("\n");
		}
	}

}
